mapper.select:
SELECT ro.*, u.user_id as u_user_id, u.real_name as user_real_name, r.room_id as r_room_id, r.room_number, a.user_id as a_user_id, a.real_name as assignee_real_name FROM repair_order ro LEFT JOIN sys_user u ON ro.user_id = u.user_id LEFT JOIN room r ON ro.room_id = r.room_id LEFT JOIN sys_user a ON ro.assignee_id = a.user_id
AND ro.user_id = #{userId}
AND ro.status = #{status}
ORDER BY ro.create_time DESC
